import React from 'react';
import '../index.css';

function ImagePopup({card, onClose}) {

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id); // Снова проверяем, есть ли лайк на этой карточке

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  return (
    <div className={`popup popup_open-card ${(Object.entries(card).length > 0) && 'popup_is-opened'}`}>
      <div className="popup__element">
        <img className="popup__image" src={card.link} alt={card.name}/>
        <button type="button" aria-label="Закрыть" className="popup__close" onClick={onClose}></button>
        <h2 className="popup__text">{card.name}</h2>
      </div>
    </div>
  )
}

export default ImagePopup;
