          noValidate
          onSubmit={onSubmit}
        >
          <h2 className="popup__title">Вход</h2>
          {children}
          <button
            type="submit"
            className="popup__submit"
            name="submit"
            defaultValue={submitText || "Сохранить"}
          >
            {isLoading ? "Сохранение..." : submitText || "Сохранить"}
          </button>
        </form>
    </div>
  );
}

export default Login;
