import React from "react";
import successImage from "../images/Success.svg";
import failImage from "../images/Fail.svg";

function InfoTooltip({ isOpen, onClose, isSuccess}) {

  return (
    <div
      className={`popup popup_edit-avatar ${isOpen && "popup_is-opened"}`}
      onClick={onClose}
    >
      <div
        className="info__container"
        onClick={(e) => {
          e.stopPropagation();
        }}
      >
        <button
          type="button"
          aria-label="Закрыть"
          className="popup__close"
          onClick={onClose}
        />
        <div
          className="popup__content"
        >
          <img className="info__image" src={successImage} alt="Место Россия"/>
          <h2 className="info__text">{isSuccess ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте ещё раз.'}</h2>
        </div>
      </div>
    </div>
  );
}

export default InfoTooltip;
