import React from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup({isOpen, onClose}) {

  const [name, setName] = React.useState('');
  const [description, setDescription] = React.useState('');

  function handleNameChange(e) {
    setName(e.target.value);
  }
  function handleDescriptionChange(e) {
    setDescription(e.target.value);
  }

  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]);

  function handleSubmit(e) {
    e.preventDefault();// Запрещаем браузеру переходить по адресу формы

    onUpdateUser({ // Передаём значения управляемых компонентов во внешний обработчик
      name,
      about: description,
    })
  } 

  return (
    <PopupWithForm classs='add-card' name='card-content' title='Новое место' submitText='Создать' 
      onClose={onClose}
      isOpen={isOpen}>
        <input 
          type="text" 
          id="card-field" 
          className="popup__field popup__field_input_card" 
          minLength="2" 
          maxLength="30" 
          required 
          placeholder="Название" 
          name="name" 
          defaultValue=""/>
        <span className="card-field-error popup__field-error"></span>
        <input 
          type="url" 
          id="link-field" 
          className="popup__field popup__field_input_link" 
          required 
          placeholder="Ссылка на картинку" 
          name="link" 
          defaultValue=""/>
        <span className="link-field-error popup__field-error"></span>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
