import React from "react";
import logo from "../images/logo__image.svg";
import { Link, Switch, Route } from 'react-router-dom';

function Header({email, handleEmailReset, headerLink}) {

  function signOut() {
    localStorage.removeItem('jwt');
    handleEmailReset();
  }

  return (
    <header className="header">
      <img className="header__image" src={logo} alt="Место Россия" />
      <div className="header__info">
        <h2 className="header__email">{email}</h2>

        <Switch>
        <Route path="/mesto-react">
          <Link onClick={signOut} to="/signin" className="header__link">{headerLink}</Link>
        </Route>
        <Route path="/signin">
        <Link to="/signup" className="header__link"></Link>
        </Route>
        <Route path="/signup">
          <Home />
        </Route>
      </Switch>
      </div>
    </header>
  );
}

export default Header;
