import React from 'react';
import '../index.css';
import {UserContext} from '../contexts/CurrentUserContext';

function Card({card, onCardClick, OnDeleteClick}) {

  const data = React.useContext(UserContext);
  const currentUser = data[0];
  //const cards = data[1];

  const isOwn = card.owner._id === currentUser._id; //наша карточка или нет
  const isLiked = card.likes.some(i => i._id === currentUser._id); //поставлен ли лайк

  // Создаём переменную, которую после зададим в `className` для кнопки лайка
  const cardLikeButtonClassName = (`card__like-button ${isLiked && 'card__like-button_active'}`);

  function handleClick() {
    onCardClick(card);
  }

  return (
    <div className="element">
        {isOwn && <button type="button" aria-label="Удалить" className="element__trash" onClick={OnDeleteClick} />} 
        <img className="element__image" src={card.link} alt={card.name} onClick={handleClick}/>
        <div className="element__group">
            <h2 className="element__text">{card.name}</h2>
            <div className="element__likes">
                <button type="button" aria-label="Нравится" className="element__like"></button>
                <div className="element__count">{card.likes.length}</div>
            </div>
        </div>
    </div>
  );
}

export default Card;
