import {useEffect, useState } from 'react';
import { useContext } from 'react';
import coursor from '../images/AvatarEditCoursor.svg';
import '../index.css';
import api from '../utils/api';
import Card from './Card';
import {UserContext} from '../contexts/CurrentUserContext';

function Main({onEditAvatar, onEditProfile, onAddPlace, handleCardClick, handleDeleteClick}) {

  const currentUser = useContext(UserContext);

  const [cards, setCards] = useState([]);
  console.log(cards);

  useEffect(() => {
      api.getInitialCards()
        .then((cards) => {
          setCards(cards);
        })
        .catch(err => console.log(err));
    }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id); //поставлен ли лайк
