import React from "react";

function Login() {
  return (
    <div>
      <form
          name={`popup__login`}
          className="popup__content"
          noValidate
          onSubmit={onSubmit}
        >
          <h2 className="popup__title">Вход</h2>
          <input
            type="text"
            id="name-field"
            className="popup__field popup__field_input_name"
            value={name || ""}
            onChange={handleNameChange}
            minLength="2"
            maxLength="40"
            required
            placeholder="Ваше имя"
            name="email"
            />
          <span className="name-field-error popup__field-error"></span>
          <input
            type="text"
            id="profession-field"
            className="popup__field popup__field_input_profession"
            value={description || ""}
            onChange={handleDescriptionChange}
            minLength="2"
            maxLength="200"
            required
            placeholder="Ваша профессия"
            name="password"
          />
          <span className="profession-field-error popup__field-error"></span>
          <button
            type="submit"
            className="popup__submit"
            name="submit"
            defaultValue={"Войти"}
          >
            {isLoading ? "Проверка..." : "Войти"}
          </button>
        </form>
    </div>
  );
}

export default Login;
