import React from 'react';
import avatar from '../images/profile__avatar.jpg';
import coursor from '../images/AvatarEditCoursor.svg';
import '../index.css';

//open profile popup
popupOpenButtonElement.addEventListener('click', function() {
  popupWithEditForm.open();
  nameInput.value = userInfo.getUserInfo().name;
  jobInput.value = userInfo.getUserInfo().about;
  formValidators['popup__profile-content'].resetValidation();
});

//open card popup
popupAddButtonElement.addEventListener('click', function() {
  popupWithAddForm.open();
  formValidators['popup__card-content'].resetValidation();
});

//open avatar popup
profileAvatar.addEventListener('click', function() {
  popupEditAvatar.open();
})


function Main() {
  return (
    <main className="content">

      <section className="profile">
        <div className="profile__image">
          <img className="profile__avatar" src={avatar} alt="аватар"/> 
          <img className="profile__edit" src={coursor} alt="кнопка редактирования"/>
        </div>
        <div className="profile__info">
          <div className="profile__title">
            <h1 className="profile__name">Жак-Ив Кусто</h1>
            <button type="button" aria-label="Редактировать" className="profile__edit-button"></button>
          </div>
          <p className="profile__profession">Исследователь океана</p>
        </div>
        <button type="button" aria-label="Добавить фотокарточку" className="profile__add-button"></button>
      </section> 

      <section className="elements">

      </section> 

    </main> 
  );
}

export default Main;
