import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import * as auth from '../auth.js';

function Login() {
  const [formValue, setFormValue] = useState({
    username: '',
    password: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formValue.username || !formValue.password){
      return;
    }
    auth.authorize(formValue.username, formValue.password)
      .then((data) => {
        if (data.jwt){
          setFormValue({username: '', password: ''});
          handleLogin();
          navigate('/diary', {replace: true});
        }
        // нужно проверить, есть ли у данных jwt
        // сбросьте стейт, затем в колбэке установите
        // стейт loggedIn родительского App как true,
        // затем перенаправьте его в /diary
      })
      .catch(err => console.log(err));
  }

  return (
    <div className="login">
      <form
          name='form__login'
          className="popup__content"
          noValidate
        //   onSubmit={onSubmit}
        >
          <h2 className="login__title">Вход</h2>
          <input
            type="text"
            id="email-field"
            className="login__field"
            value=""
            // onChange={handleNameChange}
            minLength="2"
            maxLength="50"
            required
            placeholder="Email"
            name="email"
            />
          <span className="name-field-error login__span"></span>
          <input
            type="text"
            id="password-field"
            className="login__field"
            value=""
            // onChange={handleDescriptionChange}
            minLength="2"
            maxLength="40"
            required
            placeholder="Пароль"
            name="password"
          />
          <span className="profession-field-error login__span"></span>
          <button
            type="submit"
            className="login__submit"
            name="submit"
            defaultValue="Войти"
          >
            {"Войти"}
            {/* {isLoading ? "Проверка..." : "Войти"} */}
          </button>
        </form>
    </div>
  );
}

export default Login;
